mapper.update:
update doctor
set name = #{name,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  address = #{address,jdbcType=VARCHAR},
  hid = #{hid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, sex, address, hid
from doctor
where id = #{id,jdbcType=INTEGER}
